import React, { useState } from "react";
import "../css/Poke-style.css";

const Pokemon = ({ pokemon, loading }) => {
  const [selectedPokemon, setSelectedPokemon] = useState(null);
  const [searchInput, setSearchInput] = useState("");

  const openPokeInfo = async (pokemon) => {
    setSelectedPokemon(pokemon);
  };

  const closePokeInfo = () => {
    setSelectedPokemon(null);
  };

  return (
    <>
      {selectedPokemon && (
        <div className="poke-info-container" onClick={closePokeInfo}>
          <div className="poke-info-content" onClick={(e) => e.stopPropagation()}>
            <img src={selectedPokemon.sprites.front_default} alt="pokemon" />
            <h2>{selectedPokemon.name}</h2>
            <p>Height: {selectedPokemon.height}</p>
            <p>Weight: {selectedPokemon.weight}</p>
          </div>
        </div>
      )}

      <div className="row card-row">
        {loading ? (
          <h1>Loading...</h1>
        ) : (
          pokemon
            .filter((item) => {
              if (searchInput === "") {
                return item;
              } else if (item.name.toLowerCase().includes(searchInput.toLowerCase())) {
                return item;
              }
            })
            .map((item) => {
              return (
                <div className="col-md-3" key={item.id}>
                  <div className="card poke-card" onClick={() => openPokeInfo(item)}>
                    <img className="card-img-top card-img" src={item.sprites.front_default} alt="Card image cap" />
                    <div className="card-body">
                      <h5 className="card-title poke-name">{item.name}</h5>
                    </div>
                  </div>
                </div>
              );
            })
        )}
      </div>
    </>
  );
};

export default Pokemon;
