import React from "react";
import { useState } from "react";
import { Modal } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import "../css/Poke-style.css";

const Pokemon = ({ pokemon, loading }) => {
  const [showModal, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [pokeName, setPokeName] = useState("");
  const [pokeHeight, setPokeHeight] = useState("");
  const [pokeWeight, setPokeWeight] = useState("");
  const [pokeImg, setPokeImg] = useState();

  const openPokeInfo = async (res) => {
    setPokeName(res.name);
    setPokeHeight(res.height);
    setPokeWeight(res.weight);
    setPokeImg(res.sprites.front_default);
    handleShow();
  };

  return (
    <>
      <Modal
        show={showModal}
        onHide={handleClose}
        aria-labelledby="contained-modal-title-vcenter"
        centered
        container={() => document.getElementById("root-modal")}
      >
        <Modal.Header closeButton>
          <Modal.Title>{pokeName}</Modal.Title>
        </Modal.Header>
        <Modal.Body className="poke-content">
          <img
            src={pokeImg}
            className="img-fluid img-height"
            alt="Responsive image"
          ></img>
          <p>Height : {pokeHeight}</p>
          <p>Weight : {pokeWeight}</p>
        </Modal.Body>
      </Modal>

      <div className="row card-row">
        {loading ? (
          <h1>Loading...</h1>
        ) : (
          pokemon
            .filter((item) => {
              return item;
            })
            .map((item) => {
              return (
                <div className="col-md-3" key={item.id}>
                  <div
                    className="card poke-card"
                    onClick={() => openPokeInfo(item)}
                  >
                    <img
                      className="card-img-top card-img"
                      src={item.sprites.front_default}
                      alt="Card image cap"
                    ></img>
                    <div className="card-body">
                      <h5 className="card-title poke-name">{item.name}</h5>
                    </div>
                  </div>
                </div>
              );
            })
        )}
      </div>
    </>
  );
};

export default Pokemon;
