import React, { useState } from "react";
import Modal from "react-modal";
import "../css/Poke-style.css";

const Pokemon = ({ pokemon, loading }) => {
    const [showModal, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [pokeName, setPokeName] = useState("");
    const [pokeHeight, setPokeHeight] = useState("");
    const [pokeWeight, setPokeWeight] = useState("");
    const [pokeImg, setPokeImg] = useState();

    const openPokeInfo = async (res) => {
        setPokeName(res.name);
        setPokeHeight(res.height);
        setPokeWeight(res.weight);
        setPokeImg(res.sprites.front_default);
        handleShow();
    };

    return (
        <>
            <Modal
                isOpen={showModal}
                onRequestClose={handleClose}
                contentLabel="Poke Info"
                ariaHideApp={false}
                class="poke-modal"
                style={{
                    overlay:{backgroundColor: 'rgba(255, 243, 176, 0.75)'},
                    content:{}}}
            >
                <div class="poke-content">
                    <div class="">
                        <h2>{pokeName}</h2>
                        <img
                            src={pokeImg}
                            class="img-fluid img-height"
                            alt="Responsive image"
                        ></img>
                        <p>Height : {pokeHeight}</p>
                        <p>Weight : {pokeWeight}</p>
                    </div>
                    <div>
                        <button class="btn-func" onClick={handleClose}>
                            Close
                        </button>
                    </div>
                </div>
            </Modal>

            <div class="row card-row">
                {loading ? (
                    <h1>Loading...</h1>
                ) : (
                    pokemon
                        .filter((item) => {
                            return item;
                        })
                        .map((item) => {
                            return (
                                <div class="col-md-3" key={item.id}>
                                    <div
                                        class="card poke-card"
                                        onClick={() => openPokeInfo(item)}
                                    >
                                        <img
                                            class="card-img-top card-img"
                                            src={item.sprites.front_default}
                                            alt="Card image cap"
                                        ></img>
                                        <div class="card-body">
                                            <h5 class="card-title poke-name">{item.name}</h5>
                                        </div>
                                    </div>
                                </div>
                            );
                        })
                )}
            </div>
        </>
    );
};

export default Pokemon;
