import React, { useEffect, useState } from "react";
import { Modal, Button } from "react-bootstrap";
import Pokemon from "./Pokemon";

const PokemonZoo = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [url, setUrl] = useState("https://pokeapi.co/api/v2/pokemon/");
  const [nextUrl, setNextUrl] = useState();
  const [prevUrl, setPrevUrl] = useState();
  const [disable, setDisable] = useState(true);

  const pokeFunc = async () => {
    const res = await fetch(url);
    const data = await res.json();

    setNextUrl(data.next);
    setPrevUrl(data.previous);
    getPokemonData(data.results);
    setLoading(false);

    console.log("PREV", data.previous);
    console.log("NEXT", data.next);

    if (data.previous != null) {
      setDisable(false);
    } else {
      setDisable(true);
    }
  };

  const getPokemonData = async (results) => {
    const idSet = new Set(pokeData.map(pokemon => pokemon.id)); // create a set of IDs for the current pokeData
  
    const pokemonList = await Promise.all(
      results.filter(item => !idSet.has(getPokemonId(item))) // filter out items with IDs that already exist in pokeData
             .map(async (item) => {
        const res = await fetch(item.url);
        const data = await res.json();
        return data;
      })
    );
  
    setPokeData(prevState => {
      const newState = [...prevState, ...pokemonList];
      newState.sort((a, b) => (a.id > b.id ? 1 : -1));
      return newState;
    });
  };
  
  const getPokemonId = (pokemon) => {
    return pokemon.url.split("/")[6]; // extract ID from the URL
  };
  

  useEffect(() => {
    pokeFunc();
  }, [url]);

  return (
    <>
      <nav className="navbar navbar-dark bg-dark">
        <a className="navbar-brand poke-nav" href="#">
        </a>
      </nav>
      <div className="container">
        <Pokemon pokemon={pokeData} loading={loading}></Pokemon>
        <div className="btn-div">
          <button
            type="button"
            disabled={disable}
            className="btn btn-func"
            onClick={() => {
              setPokeData([]);
              setUrl(prevUrl);
            }}
          >
            Previous
          </button>
          &nbsp;&nbsp;
          <button
            type="button"
            className="btn btn-func"
            onClick={() => {
              setPokeData([]);
              setUrl(nextUrl);
            }}
          >
            Next
          </button>
        </div>
      </div>
    </>
  );
};

export default PokemonZoo;
