import { Link, Routes, Route } from "react-router-dom"
import { Home } from "./pages/Home.js"
import { About } from "./pages/About.js"
import "./css/Layout.css"

function App() {
    const handleRefresh = () => {
        window.location.reload();
    }

    return (
        <>
            <body>
                <h1 className="header">Pokedex - your personal pokemon library!</h1>
                <nav className="navBar">
                    <Link to="/" className="navBarElement" onClick={window.location.pathname === '/' ? handleRefresh : null}>Home</Link>
                    <Link to="/About" className="navBarElement">About</Link>
                </nav>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/About" element={<About />} />
                </Routes>
            </body>
        </>
    );
}

export default App;
