import React, { useState } from "react";
import Modal from "react-modal";
import "../css/Poke-style.css";

const Pokemon = ({ pokemon, loading }) => {
    const [showModal, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [pokeId, setPokeId] = useState("");
    const [pokeName, setPokeName] = useState("");
    const [pokeHeight, setPokeHeight] = useState("");
    const [pokeWeight, setPokeWeight] = useState("");
    const [pokeAbilities, setPokeAbilities] = useState([]);
    const [pokeTypes, setPokeTypes] = useState([]);
    const [pokeStats, setPokeStats] = useState([]);
    const [pokeImg, setPokeImg] = useState();

    const openPokeInfo = async (res) => {
        setPokeId(res.id)
        setPokeName(res.name);
        setPokeHeight(res.height);
        setPokeWeight(res.weight);
        setPokeAbilities(res.abilities);
        setPokeTypes(res.types);
        setPokeImg(res.sprites.front_default);

        const stats = await fetch(res.stats[0].stat.url)
            .then(res => res.json())
            .then(data => {
                const stats = {};
                data.damage_relations.double_damage_from.forEach(type => {
                    stats[type.name] = "2x";
                });
                data.damage_relations.half_damage_from.forEach(type => {
                    stats[type.name] = "0.5x";
                });
                data.damage_relations.no_damage_from.forEach(type => {
                    stats[type.name] = "0x";
                });
                return stats;
            });

        handleShow();
    };

    return (
        <>
            <Modal
                isOpen={showModal}
                onRequestClose={handleClose}
                contentLabel="Poke Info"
                ariaHideApp={false}
                style={{
                    overlay: { backgroundColor: 'rgba(158, 42, 43, 0.4)' },
                    content: {
                        position: 'absolute',
                        top: '20%',
                        left: '20%',
                        right: '20%',
                        bottom: '40px',
                        background: 'rgba(158, 42, 43, 0)',
                        border: '0px'
                    }
                }}
            >
                <div class="poke-content">
                    <button class="btn-func btn" onClick={handleClose}>
                        X
                    </button>

                    <h2 class="poke-name">{pokeId} : {pokeName}</h2>
                    <img
                        src={pokeImg}
                        alt="Image"
                        class="poke-image"
                    ></img>
                    <div class="attribute-list">
                        <p><span class="attribute-name">Types:</span> {pokeTypes.map(type => type.type.name).join(", ")}</p>
                        <p><span class="attribute-name">Height:</span> {pokeHeight}</p>
                        <p><span class="attribute-name">Weight:</span> {pokeWeight}</p>
                        <p><span class="attribute-name">Abilities:</span> {pokeAbilities.map(ability => ability.ability.name).join(", ")}</p>
                        <p class="stat-style">Stats</p>
                        <p>
                            <table class="stat-view">
                                <tr>
                                    <th>HP</th>
                                    <th>{pokeStats.hp || "-"}</th>
                                </tr>
                                <tr>
                                    <th>Attack</th>
                                    <th>{pokeStats.attack || "-"}</th>
                                </tr>
                                <tr>
                                    <th>Defense</th>
                                    <th>{pokeStats.defense || "-"}</th>
                                </tr>
                                <tr>
                                    <th>Special Attack</th>
                                    <th>{pokeStats["special-attack"] || "-"}</th>
                                </tr>
                                <tr>
                                    <th>Special Defense</th>
                                    <th>{pokeStats["special-defense"] || "-"}</th>
                                </tr>
                                <tr>
                                    <th>Speed</th>
                                    <th>{pokeStats.speed || "-"}</th>
                                </tr>
                            </table>
                        </p>
                    </div>
                </div>
            </Modal >

            <div class="row align">
                {loading ? (
                    <h1>Loading...</h1>
                ) : (
                    pokemon
                        .filter((item) => {
                            return item;
                        })
                        .map((item) => {
                            return (
                                <div class="col" key={item.id}>
                                    <div class="poke-card" onClick={() => openPokeInfo(item)}>
                                        <img
                                            src={item.sprites.front_default}
                                            alt="Poke-card image"
                                        />
                                        <div>
                                            <h5 class="poke-name">{item.name}</h5>
                                        </div>
                                    </div>
                                </div>
                            );
                        })
                )}
            </div>

        </>
    );
};

export default Pokemon;
